import { Button, TextField } from '@mui/material'
import axios from 'axios'
import React from 'react'
import { useRef, useState } from 'react'
import { Link } from 'react-router-dom'
import Precedent from '../components/Precedent'
import Rendezvous from './Rendezvous'

function Login() {

  const [abc, setAbc] = useState(false);
 let content ="";


  const codeField = useRef('');
  let formData = new FormData;
  
  const HandleSubmit = (e)=>{
    e.preventDefault();
    
    formData.append('code', codeField.current.value)
    
    axios.post('http://localhost/brief6/Backend/user/login', formData)
    .then(function(response){
      let data = response.data;
      if(data === "True")
      {
        content = <Rendezvous />;
       
      }else{
        content=  <div>
        <Precedent />
        <form onSubmit={HandleSubmit} className='btn__Code'>
          <TextField className='form__input' label='Code' variant='filled' inputRef={codeField}></TextField>
          <Button type='submit' variant='contained' color='primary'>Submit</Button>
          {/* <Link to='/Rendezvous' style={{textDecoration:"none", alignSelf:"center"}}><Button type='submit' variant='contained' color='primary'>Submit</Button></Link> */}
        </form>
      </div>;
        console.log(data);
      }
    })
    .catch(function(error){
      console.log(error);
    })

    // console.log(codeField.current.value);
  }

  return (
    <div className='parentLogin'>
      <h1>Hello</h1>
     {content}
    </div>
  )
}

export default Login
