import { Button, TextField } from '@mui/material'
import axios from 'axios';
import React, { useRef } from 'react'
import { Link } from 'react-router-dom'
import img_back from '../imgs/previous.png'

function AddApointment() {

  const sujet = useRef('');
  const date = useRef('');
  const creneau = useRef('');

  const formData = new FormData;

  const HandleSubmit = ()=>{
    formData.append('code', localStorage.getItem('myCode'));
    formData.append('sujet', sujet.current.value);
    formData.append('date', date.current.value);
    formData.append('creneau', creneau.current.value);

    axios.post('http://localhost/brief6/Backend/reservation/rendezvous', formData)
    .then(function(response){
      console.log(response);
    })
    .catch(function(error){
      console.log(error);
    })

  }

// console.log(localStorage.getItem('code'));
  return (
    <div>
      <Link to='/Rendezvous'><img src={img_back} width='45' className='img_back'/></Link>
      <div className='form__add'>
        <form onSubmit={HandleSubmit} className='form'>

          <div style={{backgroundColor:"blue", alignSelf:"center", marginLeft:80}}>
            <label>Date</label>
            <input type='date' ref={date} style={{width:300, padding:6}}/>
          </div>

          <div style={{backgroundColor:"red", alignSelf:"center", marginLeft:80}}>
            <label>Creneau</label>
            <select ref={creneau} style={{width:300, padding:6}}>
              <option>9 - 10</option>
              <option>10 - 11</option>
              <option>11 - 12</option>
              <option>14 - 15</option>
              <option>15 - 16</option>
              <option>16 - 17</option>
            </select>
          </div>

          <TextField
            className='form__input'
            label='Sujet'
            variant='outlined'
            type='name'
            required
            multiline
            rows='4'
            inputRef={sujet}
            >
          </TextField>
          <Button type='submit' variant='contained' color='primary' className='form_submit'>Submit</Button>
        </form>
      </div>
    </div>
  )
}

export default AddApointment
