import { Button, TextField } from '@mui/material'
import React, { useRef } from 'react'
import { Link } from 'react-router-dom'
import img_back from '../imgs/previous.png'

function AddApointment() {

  const sujet = useRef('');
  const date = useRef('');
  const debut = useRef('');
  const fin = useRef('');

  const formData = new FormData;

  const HandleSubmit = ()=>{
    formData.append('code', localStorage.getItem('code'));
    formData.append('sujet', sujet.current.value);
    formData.append('date', date.current.value);
    formData.append('debut', debut.current.value);
    formData.append('fin', fin.current.value);
  }

// console.log(localStorage.getItem('code'));
  return (
    <div>
      <Link to='/Rendezvous'><img src={img_back} width='45' className='img_back'/></Link>
      <div className='form__add'>
        <form onSubmit={HandleSubmit} className='form'>
          <input type='date' className='form__input' style={{width:300, padding:6}}/>
          <TextField 
            className='form__input'
            label='Sujet'
            variant='outlined'
            type='name'
            required
            multiline
            rows='4'
            inputRef={sujet}
            >
          </TextField>
          <Button type='submit' variant='contained' color='primary' className='form_submit'>Submit</Button>
        </form>
      </div>
    </div>
  )
}

export default AddApointment
